mapper.select:
select c.id, c.teaId ,c.checked,c.className,c.classTime,c.teacherInfo,c.courseInfo,c.mainLecturer,c.mydate,a.checkStr,c.shenQingRenId,c.shenQingRenName from ClassApplication c left join Application_Check a on c.checked = a.id
and c.shenQingRenId= #{shenQingRenId}
and c.className like "%"#{className}"%"
and c.checked=#{tabKey}

select c.id, c.teaId
,c.checked,c.className,c.classTime,c.teacherInfo,c.courseInfo,c.mainLecturer,c.mydate,a.checkStr,c.shenQingRenId,c.shenQingRenName
from ClassApplication c
left join Application_Check a
on c.checked = a.id


        and c.shenQingRenId=#{shenQingRenId}


        and c.className=#{className}


        and c.checked=#{tabKey}

    and cast(c.mydate as date) = #{date}
