mapper.select:
select s.id,s.stuId,s1.stuName,s.reason, s.mydate,a2.checkStr,a1.category,s.checked,c.className from course.StudentApplication s left join Application_Category a1 on s.states = a1.id left join Application_Check a2 on s.checked = a2.id left join course.XjtuUserInfo s1 on s.stuId = s1.userno left join Course c on s.classId = c.classId
s.checked=#{key}
and s.stuId = #{stuId}

select s.id,s.stuId,s1.username,s.reason, s.mydate ,a2.checkStr,a1.category,s.checked,c.className from course.StudentApplication s left join Application_Category a1 on s.states = a1.id left join Application_Check a2 on s.checked = a2.id left join course.XjtuUserInfo s1 on s.stuId = s1.userno left join Course c on s.classId = c.classId
s.checked=#{key}
and s.stuId = #{stuId}
and cast(s.mydate as date) = #{date}
