mapper.update:
update ClassAll
schoolDistrictId = #{classAll.schoolDistrictId}, className = #{classAll.className}, instructorId = #{classAll.instructorId}, instructorName = #{classAll.instructorName}, startWeek = #{classAll.startWeek}, endWeek = #{classAll.endWeek}, classDateDesc = #{classAll.classDateDesc}, classPlaceId = #{classAll.classPlaceId}, classPlaceName = #{classAll.classPlaceName}, conflictDesc = #{classAll.conflictDesc}, scheduled = #{classAll.scheduled}, operatorId = #{classAll.operatorId}, operatorName = #{classAll.operatorName}
where id = #{classAll.id}

update ClassAll
schoolDistrictId = null, className = '1Áè≠', instructorId = null, instructorName = null, startWeek = null, endWeek = null, classDateDesc = null, classPlaceId = null, classPlaceName = null, conflictDesc = null, scheduled = 0, operatorId = #{operatorId}, operatorName = #{operatorName}
where id = #{id}

mapper.select:
select distinct (ClassAll.departId),Department.departName
from ClassAll
join Department
on ClassAll.departId=Department.departId


        and academicYear = #{academicYear}



        and classSemester like #{classSemesterLike}

select distinct (ClassAll.classPlaceName)
from ClassAll


        and academicYear = #{academicYear}



        and classSemester like #{classSemesterLike}

select * from course.ClassAll


        and classPlaceName = #{roomName}


        and academicYear = #{academicYear}



        and classSemester like #{classSemesterLike}


