mapper.update:
update oral
set flag=0
AND operatorId=#{operatorId}
AND  operatorName=#{operatorName}
where id=#{id}

update oral
set teaId=#{teaId}
and teaName=#{teaName}
and testTime=#{time}
and examRoom=#{examRoom}
and operatorId=#{operatorId}
and operatorName=#{operatorName}
and stuLimit=#{stuLimit}
and place=#{place}
where id=#{id}

update oral
set curChoose=curChoose+1
where id=#{oralId}

mapper.delete:
delete *
from oral_choose
where oralId=#{oralId}
and stuId=#{stuId}
